    public static ArrayList<Wisata> getListData(){
        Wisata wisata;
        ArrayList<Wisata> list = new ArrayList<>();

        for (String[] mData : data){
            wisata = new Wisata();
            wisata.setWtitle(mData[0]);
            wisata.setWdesc(mData[1]);
            wisata.setWimage(mData[2]);

            list.add(wisata);
        }
        return  list;
    }
